import React from "react";

const Info = ({
  sex,
  surname,
  balance,
}: {
  sex: string;
  surname: string;
  balance: number;
}) => {
  interface Human {
    human: {
      sex: "man" | "woman";
      surname: string;
    };
    accountBalance: number;
  }
  const initData: Human = {
    human: {
      sex: "man",
      surname: "Detic",
    },
    accountBalance: 1000,
  };

  const [data, setData] = useState<Human>(initData);

  const handleSubtractCoin = () => {
    setData((prev): Human => {
      let newData: Human = { ...prev };
      newData.accountBalance = newData.accountBalance - 500;
      return newData;
    });
  };
  const handleAddCoin = () => {
    setData((prev): Human => {
      console.log(prev);
      let newData: Human = { ...prev };
      newData.accountBalance = newData.accountBalance + 500;
      return newData;
    });
  };
  const handleSexChange = () => {
    setData((prev): Human => {
      console.log(prev);
      let newData: Human = { ...prev };
      if (newData.human.sex === "man") {
        newData.human.sex = "woman";
      } else if (newData.human.sex === "woman") {
        newData.human.sex = "man";
      }
      console.log(prev);
      return newData;
    });
  };

  return (
    <div className="container">
      <div>{sex}</div>
      <div>{surname}</div>
      <div>{balance}</div>
    </div>
  );
};

export default Info;
