import React from "react";
import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";
import Info from "./components/info/info";
import { Z_HUFFMAN_ONLY } from "zlib";
function App() {
  interface Human {
    human: {
      sex: "man" | "woman";
      surname: string;
    };
    accountBalance: number;
  }
  const initData: Human = {
    human: {
      sex: "man",
      surname: "Detic",
    },
    accountBalance: 1000,
  };

  const [data, setData] = useState<Human>(initData);

  const handleSubtractCoin = () => {
    setData((prev): Human => {
      let newData: Human = { ...prev };
      newData.accountBalance = newData.accountBalance - 500;
      return newData;
    });
  };
  const handleAddCoin = () => {
    setData((prev): Human => {
      let newData: Human = { ...prev };
      newData.accountBalance = newData.accountBalance + 500;
      return newData;
    });
  };
  const handleSexChange = () => {
    setData((prev): Human => {
      console.log(prev);
      let newData: Human = { ...prev };
      if (newData.human.sex === "man") {
        newData.human.sex = "woman";
      } else if (newData.human.sex === "woman") {
        newData.human.sex = "man";
      }
      console.log(prev);
      return newData;
    });
  };

  return (
    <div className="App">
      <Info
        sex={data.human.sex}
        surname={data.human.surname}
        balance={data.accountBalance}
      ></Info>
      <button onClick={handleSubtractCoin}>Subtract</button>
      <button onClick={handleAddCoin}>Add</button>
      <button onClick={handleSexChange}>Swap Sex</button>
    </div>
  );
}

export default App;
